import{_ as t,o as e,c as a,Q as i}from"./chunks/framework.c6ce8c78.js";const _=JSON.parse('{"title":"在 H5 页面中使用新拟态 UI 设计","description":"","frontmatter":{},"headers":[],"relativePath":"content/docs/10.md","lastUpdated":1711938976000}'),r={name:"content/docs/10.md"},c=i('<h1 id="在-h5-页面中使用新拟态-ui-设计" tabindex="-1">在 H5 页面中使用新拟态 UI 设计 <a class="header-anchor" href="#在-h5-页面中使用新拟态-ui-设计" aria-label="Permalink to &quot;在 H5 页面中使用新拟态 UI 设计&quot;">​</a></h1><h4 id="什么是新拟态-ui-设计" tabindex="-1">什么是新拟态 UI 设计？ <a class="header-anchor" href="#什么是新拟态-ui-设计" aria-label="Permalink to &quot;什么是新拟态 UI 设计？&quot;">​</a></h4><p>在设计达人看来这种 UI 很适合用来做概念设计的产品，当然实际应用中也可以尝试，因为视觉效果确实是很不错呢。我们来看看关于新拟态的 UI 作品。</p><p><img src="http://cdn.bingkele.cc/FqEiGVf1w1jnQrWzXdJmksmL5AjV" alt=""></p><p><img src="http://cdn.bingkele.cc/FrPzNEygXHIODJcCXP5c-jERY8yW" alt=""></p><p><img src="http://cdn.bingkele.cc/Fqvz8xA244ZRhuMkKlh9Z1kjvLh-" alt=""></p><p><img src="http://cdn.bingkele.cc/Fkl7V_V_7D1uVb06zWxNZZUJQXg3" alt=""></p><p><img src="http://cdn.bingkele.cc/Fh_e0131VEt8lJn3s16Ue4gj7RH0" alt=""></p><h4 id="如何实现新拟态-ui-设计效果" tabindex="-1">如何实现新拟态 UI 设计效果？ <a class="header-anchor" href="#如何实现新拟态-ui-设计效果" aria-label="Permalink to &quot;如何实现新拟态 UI 设计效果？&quot;">​</a></h4><ol><li><p><strong>直接参考下图的设置：</strong></p><p>大家先看这个图，新拟态主要是通过阴影来实现的，通过阴影实现凸凹两种质感</p><p><img src="http://cdn.bingkele.cc/FozWo_oYl6qQAJta0vlHE84ExiG-" alt=""></p></li><li><p><strong>使用 Neumorphism 在线工具：</strong></p><p>推荐大家用 Neumorphism 在线工具来快速实现新拟态 UI 效果，因为它带有预览功能，所见即所得，比 PS 或 Sketch 上设置更加快捷，而具体的设置参数看对应 CSS 属性值就可以了，也可以直接复制 CSS 样式代码。</p><p><img src="http://cdn.bingkele.cc/Fsz33gxOHL6jbCAk4PGuich3AZzy" alt=""></p><blockquote><p>Neumorphism 地址：<a href="https://neumorphism.io/" target="_blank" rel="noreferrer">https://neumorphism.io/</a></p></blockquote></li></ol><h4 id="新拟态-ui-主要实现方式" tabindex="-1">新拟态 UI 主要实现方式 <a class="header-anchor" href="#新拟态-ui-主要实现方式" aria-label="Permalink to &quot;新拟态 UI 主要实现方式&quot;">​</a></h4><ol><li><p>给图形添加 2 个阴影，参数要设置大一些，注意两阴影颜色是同一色调，然后分别设置一个亮色一暗色。这里就不说参数了，如果你调整不出效果，表示你色感方面还需要加强。</p><p><img src="http://cdn.bingkele.cc/FmU6rzIt9KuBzJWZuqDk1rxtMWTq" alt=""></p></li><li><p>模糊设置：Blur 模糊值通常是坐标值的 2 倍，这样效果会明示一点。</p><p><img src="http://cdn.bingkele.cc/FreMjAnIQ16gJJ0O1-QYRmR9iTBS" alt=""></p></li><li><p>凸出的效果就是利用渐变来设置了。</p><p><img src="http://cdn.bingkele.cc/Fm5T3oRdqD0MuUfXtTmBBgjhK8Pj" alt=""></p></li></ol><blockquote><p>原文地址：<a href="https://www.shejidaren.com/neumorphism-soft-ui.html" target="_blank" rel="noreferrer">https://www.shejidaren.com/neumorphism-soft-ui.html</a></p></blockquote>',13),o=[c];function l(p,n,s,h,m,d){return e(),a("div",null,o)}const g=t(r,[["render",l]]);export{_ as __pageData,g as default};
